import React, { Component } from "react";
import Contacts from "./components/contacts"; //<Contacts contacts={this.state.contacts} />
import SearchBox from "./components/searchbox";

import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.onClick = this.onClick.bind(this);
    this.state = { repositories: [] };
  }

  onClick(event) {
    fetch(
      "https://api.github.com/search/repositories?sort=stars&order=desc&q=" +
        this.searchBox.value
    )
      .then((res) => res.json())
      .then((data) => {
        this.setState({ repositories: data.items });
      });
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
        <form>
          <input
            type="text"
            className="searchbox"
            ref={(input) => {
              this.searchBox = input;
            }}
          />
          <button onClick={this.onClick}>Search</button>
        </form>
        <SearchBox repositories={this.state.repositories} />
      </div>
    );
  }
}

export default App;
