import React, { Component } from "react";
import SearchBox from "./components/searchbox";
import CodeSearch from "./components/codesearch";

import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.onClick = this.onClick.bind(this);
    this.state = { repositories: [] };
  }

  grabRepositories(event) {
    fetch(
      "https://api.github.com/search/repositories?sort=stars&order=desc&q=" +
        this.searchBox.value
    )
      .then((res) => res.json())
      .then((data) => {
        this.setState({ repositories: data.items });
      });
    event.preventDefault();
  }

  grabCode(event) {
    fetch(
      "https://api.github.com/search/code?sort=stars&order=desc&q=" +
        this.searchBox.value +
        "+in:file+language:js+repo:jquery/jquery" //issue here is I am specifying js files and inside of jquery repository
    )
      .then((res) => res.json())
      .then((data) => {
        this.setState({ code: data.items });
      });
    event.preventDefault();
  }

  onClick(event) {
    this.grabRepositories(event);
    this.grabCode(event);
  }

  render() {
    return (
      <div className="App">
        <form>
          <input
            type="text"
            className="searchbox"
            ref={(input) => {
              this.searchBox = input;
            }}
          />
          <button onClick={this.onClick}>Search</button>
        </form>
        <SearchBox repositories={this.state.repositories} />
        <CodeSearch items={this.state.code} />ÃŸ
      </div>
    );
  }
}

export default App;
